mapper.select:
SELECT menuno, name, explanation, price, photo, recom, storeno, dc, saleprice, point
FROM menu
WHERE menuno = #{menuno}

mapper.update:
UPDATE menu
SET recom = recom + 1
WHERE menuno = #{menuno}

UPDATE menu
SET recom = recom - 1
WHERE menuno = #{menuno}

mapper.select:
SELECT menuno, name, explanation, price, photo, recom, storeno, dc, saleprice, point, r FROM ( SELECT menuno, name, explanation, price, photo, recom, storeno, dc, saleprice, point, rownum AS r FROM ( SELECT menuno, name, explanation, price, photo, recom, storeno, dc, saleprice, point FROM menu WHERE 1=1
AND (UPPER(name) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(explanation) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY menuno DESC ) WHERE rownum <= #{end_num} ) WHERE r >= #{start_num}
