mapper.update:
UPDATE survey
SET topic = #{topic}, startdate = #{startdate}, enddate = #{enddate}, poster = #{poster},
    postersaved = #{postersaved}, posterthumb = #{posterthumb}, postersize = #{postersize},
    continueyn = #{continueyn}
WHERE surveyno = #{surveyno}

mapper.select:
SELECT *
FROM (
    SELECT t.*, ROWNUM AS rn
    FROM (
        SELECT surveyno, topic, startdate, enddate, poster, postersaved, posterthumb, postersize, cnt, continueyn
        FROM survey
        WHERE topic LIKE '%' || #{keyword} || '%'
        ORDER BY surveyno DESC
    ) t
    WHERE ROWNUM <= #{endRow}
)
WHERE rn >= #{startRow}
