mapper.select:
SELECT * FROM surveyitem
WHERE surveyno = #{surveyno}
ORDER BY itemseq ASC

mapper.update:
UPDATE surveyitem
SET item = #{item}, itemseq = #{itemseq}
WHERE surveyitemno = #{surveyitemno}

UPDATE surveyitem
SET itemcnt = itemcnt + 1
WHERE surveyitemno = #{surveyitemno}

UPDATE surveyitem
SET itemseq = itemseq + 1
WHERE surveyitemno = #{surveyitemno}
